import { useEffect, useState } from 'react';
import { Sun, Moon } from 'lucide-react';

export default function ThemeToggle() {
  const [dark, setDark] = useState(false);

  useEffect(() => {
    const root = document.documentElement;
    if (dark) {
      root.classList.add('dark');
    } else {
      root.classList.remove('dark');
    }
  }, [dark]);

  return (
    <button className="theme-toggle-button" onClick={() => setDark(!dark)}>
      {dark ? <Moon size={18} /> : <Sun size={18} />}
    </button>
  );
}

.theme-toggle-button {
  position: fixed;
  top: 1rem;
  right: 1.5rem;
  padding: 0.5rem;
  border: none;
  border-radius: 50%;
  background: transparent;
  cursor: pointer;
  z-index: 1000;
  color: var(--foreground);
}

const listRef = useRef<HTMLDivElement | null>(null);

useEffect(() => {
  if (listRef.current) {
    gsap.fromTo(
      listRef.current.children,
      { opacity: 0, y: 30 },
      {
        opacity: 1,
        y: 0,
        stagger: 0.1,
        duration: 0.4,
        ease: 'power2.out'
      }
    );
  }
}, [filteredPosts]);

const postRef = useRef<HTMLElement | null>(null);

useEffect(() => {
  if (postRef.current) {
    gsap.fromTo(
      postRef.current.children,
      { opacity: 0, y: 30 },
      {
        opacity: 1,
